mapper.select:
select call_mobile,count(id) as callTimes from dx_call_detail_client t where call_id IN
#{callIds}
GROUP BY t.call_mobile order by callTimes desc LIMIT 0,10

select call_mobile as mobile,count(id) as callTimes from dx_call_detail_client t where call_id IN
#{callIds}
GROUP BY t.call_mobile order by callTimes desc LIMIT 0,10

select call_mobile as mobile,sum(SECOND(call_time_cost)+MINUTE(call_time_cost)*60+HOUR(call_time_cost)*3600) as callDuration from dx_call_detail_client t where call_id IN
#{callIds}
GROUP BY t.call_mobile order by callDuration desc LIMIT 0,10

select count(id) from dx_call_detail_client t where call_id IN
#{callIds}
and HOUR(call_time)>=0 and HOUR(call_time) <6

select sum(SECOND(call_time_cost)+MINUTE(call_time_cost)*60+HOUR(call_time_cost)*3600) from dx_call_detail_client t where call_id IN
#{callIds}
and HOUR(call_time)>=0 and HOUR(call_time) <6

select count(dt) from (select DATE(call_time)as dt from dx_call_detail_client where call_id IN
#{callIds}
and call_type =1 GROUP BY dt ) as tb

select sum(sd) from ( select (if(mon=Month(createtime),DAY(createtime)-count(d),DAY(last_day(d))-count(d))) as sd,mon from ( select t1.create_time as createtime,DATE(t.call_time) as d, Month(str_to_date(t1.call_date,'%Y%m')) as mon from dx_call_client t1 left outer join dx_call_detail_client t on t1.id=t.call_id where t.call_id IN
#{callIds}
group by d ) as tb group by mon ) as tb2

select count(d) as talkDays,sum(dt) as talkDuration,sum(dc) as talkTimes
from (
		select DATE(call_time) as d,
			call_mobile as mdn,
			sum(SECOND(call_time_cost)+MINUTE(call_time_cost)*60+HOUR(call_time_cost)*3600) as dt,
			count(call_time) as dc
		from dx_call_detail_client t
		where call_id IN 

				#{callIds}

		and call_mobile=#{phone}
		group by d ,call_mobile
	) as tab

select (select count(call_time) from dx_call_detail_client t where call_id IN
#{callIds}
and call_type=0 and HOUR(call_time)>=0 and HOUR(call_time) <6 )as callTimes, (select count(call_time) from dx_call_detail_client t where call_id IN
#{callIds}
and call_type=1 and HOUR(call_time)>=0 and HOUR(call_time) <6 )as calledTimes, sum(SECOND(call_time_cost)+MINUTE(call_time_cost)*60+HOUR(call_time_cost)*3600) as talkDuration from dx_call_detail_client t where call_id IN
#{callIds}
and HOUR(call_time)>=0 and HOUR(call_time) <6

select count(d) as talkDays,mdn as phone,sum(dt) as talkDuration,sum(dc) as talkTimes,(select count(1) from dx_call_detail_client t where call_id IN
#{callIds}
and call_type ='0' and call_mobile=mdn ) as callTimes, (select count(1) from dx_call_detail_client t where call_id IN
#{callIds}
and call_type ='1' and call_mobile=mdn ) as calledTimes from ( select DATE(call_time) as d,call_mobile as mdn ,sum(SECOND(call_time_cost)+MINUTE(call_time_cost)*60+HOUR(call_time_cost)*3600) as dt,count(call_time) as dc from dx_call_detail_client t where call_id IN
#{callIds}
group by d ,call_mobile) t2 group by phone order by talkDays desc
